import React from 'react'
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BsGithub, BsDiscord,  } from "react-icons/bs";
import { CgWebsite } from "react-icons/cg";
import '../Projects.css'

import myProjects from '../projects.json'

const SingleProject = ({ project }) => {
  return (
    <li>
      <a href="" class="card">
        <img src={project.img} class="card__image" alt="" />
        <div class="card__overlay">
          <div class="card__header">
            <div class="card__header-text">
              <h3 class="card__title">{project.title}</h3>            
            </div>
          </div>
          <p class="card__description">{project.description}</p>
          <ul className='social-share'>
            <li><a href="https://github.com/codester1000" target="_blank"><BsGithub size={50} /></a></li>
            {project.website && <li><a href="" target="_blank"><CgWebsite size={50} /></a></li>}
            {project.discord && <li><a href="https://discordapp.com/users/692547174839353405" target="_blank"><BsDiscord size={50} /></a></li>}
          </ul>
        </div>
      </a>      
    </li>
  )
}

const Projects = () => {

  const projectsList = myProjects.map((project) => {
    return <SingleProject project={project} />
  })

  return (
    <section id="projects">
      <ul class="cards">
        {projectsList}
      </ul>
    </section>
  )
}

export default Projects
