import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';

const Card = ({ image, text }) => {
  return (
    <div class="min-h-screen w-full bg-gray-300">
      <div class="max-w-screen-md mx-auto px-10 pt-20">
        <div class="bg-white md:h-48 rounded-lg shadow-md flex flex-wrap flex-col-reverse md:flex-col">
          <div class="w-full md:w-1/2 p-4">
            <img src="https://www.mcdonalds.com/content/dam/usa/nfl/assets/nav/arches-logo_108x108.jpg" alt="" class="w-8" />
            <h3 class="text-3xl font-bold">Fast Food Cola</h3>
            <p>Get all your McDonald's favorites delivered right to your doorstep with McDelivery® on Uber Eats or DoorDash.</p>
          </div>
          <div class="w-full md:w-1/2 p-4 md:p-0">
            <img src="https://isometric.online/wp-content/uploads/2020/04/food_svg.svg" alt="" class="w-64 mx-auto" />
          </div>
        </div>
      </div>
    </div>
  )
}

const Languages = () => {
  const proficientText = ["CSS", "React", "HTML", "JavaScript", "NodeJS", "MongoDB", "DiscordJs"]
  const proficientImages = [
    "https://camo.githubusercontent.com/edc736634dd35b0f4008e2f7db456136b9fc0e1e7a4078bb72c7352b1bdf8a7e/68747470733a2f2f776f726c64766563746f726c6f676f2e636f6d2f6c6f676f732f6373732d332e737667",
    "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1200px-React-icon.svg.png",
    "https://www.w3.org/html/logo/downloads/HTML5_Badge_512.png",
    "https://upload.wikimedia.org/wikipedia/commons/thumb/9/99/Unofficial_JavaScript_logo_2.svg/2048px-Unofficial_JavaScript_logo_2.svg.png",
    "https://seeklogo.com/images/N/nodejs-logo-FBE122E377-seeklogo.com.png",
    "https://www.kindpng.com/picc/m/385-3850482_mongodb-logo-png-transparent-png.png",
    "https://cdn.logojoy.com/wp-content/uploads/20210422095037/discord-mascot.png"
  ]
  const exploringText = ["Blender", "PostgreSQL", "VueJS", "Solidity"]

  const proficiency = []
  for (let i=0; i < proficientText.length; i++) {
    proficiency.push({
      text: proficientText[i],
      image: proficientImages[i]
    })
  }
  console.log(proficiency)
  const listofProficient = proficiency.map((card) => {
    return <Card text={card.text} image={card.image} />
  })

  return (
    <section id="languages">
      <h4>MY PROFICIENCIES</h4>
      <div className='proficiencies'>
        {listofProficient}
      </div>
      <h4>WHAT I'M EXPLORING</h4>
      <div>
      </div>
    </section>
  )
}

export default Languages;
